import B from './b';
import C from './c';
import { Consumer } from './home';
import { withRouter } from 'react-router-dom';

// import Router from './router';

class Show extends Component {

  render() {
    const value = 'I will say Hello!~';

    return (

      <div>
        <Consumer>
          {(value) => {
            <div>
              <B value={value} />
              <C />
            </div>
          }}
        </Consumer>
      </div>

    )
  }
}

export default withRouter(Show);
